mapper.select:
SELECT member_id, game_count, point, create_date, secret_number, game_over
FROM pointinfo
WHERE member_id = #{memberId}

mapper.update:
UPDATE pointinfo
SET secret_number = #{secretNumber}
WHERE member_id=#{memberId}

UPDATE pointinfo
SET game_count = 0, game_over = 0
WHERE member_id=#{memberId}

UPDATE pointinfo 
SET create_date = CURRENT_DATE
WHERE member_id = #{memberId}
